mapper.select:
SELECT MEMBER_NO, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL, MEMBER_AUTH, MEMBER_LOCATION , COMPANY_NAME, COMPANY_LOCATION, PRESIDENT_NAME, BROKER_NO, PRESIDENT_PHONE FROM MEMBER M
JOIN BROKER_INFO B USING(MEMBER_NO)
WHERE MEMBER_NO = #{memberNo}

SELECT COMPANY_NAME, COMPANY_LOCATION, PRESIDENT_NAME, BROKER_NO, PRESIDENT_PHONE
FROM "BROKER_INFO"
WHERE MEMBER_NO = #{memberNo}

SELECT * FROM "STOCK_INFO"
WHERE MEMBER_NO = #{memberNo}
ORDER BY STOCK_NO DESC

SELECT IMG_URL, IMG_ORIGIN_NAME FROM "STOCK_IMG"
WHERE STOCK_NO = #{stockNo}
ORDER BY IMG_ORDER

mapper.update:
UPDATE "STOCK_COORD" SET
LAT = #{lat},
LNG = #{lng}
WHERE STOCK_NO = #{stockNo}

UPDATE "STOCK_IMG" SET
	IMG_URL = #{finalPath},
	IMG_ORIGIN_NAME = #{originalName},
	IMG_RENAME = #{rename}
	WHERE IMG_ORDER = 0
	AND STOCK_NO = #{stockNo}

UPDATE "STOCK_IMG" SET
	IMG_URL = #{finalPath},
	IMG_ORIGIN_NAME = #{originalName},
	IMG_RENAME = #{rename}
	WHERE IMG_ORDER = 1
	AND STOCK_NO = #{stockNo}

UPDATE "STOCK_IMG" SET
	IMG_URL = #{finalPath},
	IMG_ORIGIN_NAME = #{originalName},
	IMG_RENAME = #{rename}
	WHERE STOCK_NO = #{stockNo}
	AND IMG_ORDER = #{stockOrder}
