mapper.select:
SELECT COUNT(*) FROM BOARD B JOIN "MEMBER" M ON B.MEMBER_NO = M.MEMBER_NO JOIN BOARD_TOWN BT ON B.TOWN_NO = BT.TOWN_NO JOIN BOARD_CITY CT ON BT.CITY_NO = CT.CITY_NO WHERE B.BOARD_DEL_FL = 'N' AND M.MEMBER_DEL_FL = 'N'
AND BOARD_SUBJECT = #{boardSubject}
AND BT.TOWN_NO = #{townNo}
AND CT.CITY_NO = #{cityNo}
AND BOARD_TITLE LIKE '%' || #{query} || '%'
AND REGEXP_REPLACE(BOARD_CONTENT, '<[^>]*>', '') LIKE '%' || #{query} || '%'
AND ( BOARD_TITLE LIKE '%' || #{query} || '%' OR REGEXP_REPLACE(BOARD_CONTENT, '<[^>]*>', '') LIKE '%' || #{query} || '%' )
AND MEMBER_NICKNAME LIKE '%' || #{query} || '%'
ORDER BY B.BOARD_NO DESC

SELECT B.BOARD_NO, B.BOARD_TITLE, B.BOARD_CONTENT, B.READ_COUNT, B.MEMBER_NO, M.MEMBER_NICKNAME, M.MEMBER_IMG, TO_CHAR(B.BOARD_WRITE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS') AS BOARD_WRITE_DATE,
/* 좋아요 수 */ ( SELECT COUNT(*) FROM "LIKE" WHERE BOARD_NO = #{boardNo} ) AS LIKE_COUNT,
/* 특정 회원의 좋아요 여부 */ ( SELECT COUNT(*) FROM "LIKE" WHERE BOARD_NO = #{boardNo} AND MEMBER_NO = #{memberNo} ) AS LIKE_CHECK,
/* 시군구 FK */ B.TOWN_NO,
/* 군·구명 */ ( SELECT BT.TOWN_NAME FROM "BOARD_TOWN" BT WHERE BT.TOWN_NO = B.TOWN_NO ) AS TOWN_NAME,
/* 시·도 번호 */ ( SELECT BT.CITY_NO FROM "BOARD_TOWN" BT WHERE BT.TOWN_NO = B.TOWN_NO ) AS CITY_NO,
/* 시·도 이름 */ ( SELECT BC.CITY_NAME FROM "BOARD_CITY" BC JOIN "BOARD_TOWN" BT ON BC.CITY_NO = BT.CITY_NO WHERE BT.TOWN_NO = B.TOWN_NO ) AS CITY_NAME
,B.BOARD_SUBJECT FROM "BOARD" B JOIN "MEMBER" M ON B.MEMBER_NO = M.MEMBER_NO WHERE B.BOARD_DEL_FL = 'N' AND M.MEMBER_DEL_FL = 'N' AND B.BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM "LIKE"
WHERE MEMBER_NO = #{memberNo}
AND
BOARD_NO = #{boardNo}
